#define N 10//每次把最小的数提到最前，一共需要N-1次void main(){    int a[N], i, j, k, x;    printf("Input 10 numbers:\n");    for (i = 0; i < N; i++)        scanf("%d", &a[i]);    for (i = 0; i < N - 1; i++)    {        k = i;        for (j = i + 1; j < N; j++)            if (a[j] < a[k])  k = j;        if (i != k)        {            x = a[i]; a[i] = a[k]; a[k] = x;        }    }    printf("The sorted numbers:\n");        for (i = 0; i < N; i++)            printf("%d ", a[i]);}